mapper.select:
SELECT c.id, fu.avatar, fu.nickname AS from_nickname, tu.nickname AS to_nickname, c.content, c.like_number, COALESCE(r.title, q.title) as title, c.type, c.is_check, c.create_time FROM d_comment c LEFT JOIN d_agree_record r ON c.type_id = r.id LEFT JOIN d_quiz q ON c.type_id = q.id LEFT JOIN u_user fu ON c.from_uid = fu.id LEFT JOIN u_user tu ON c.to_uid = tu.id
c.type = #{query.type}
and fu.nickname LIKE concat('%', #{query.fromNickname} ,'%')
and c.is_check = #{query.isCheck}
and r.title like concat('%',#{query.title},'%') or q.title like concat('%',#{query.title},'%')
ORDER BY c.id DESC

SELECT id
FROM d_comment
WHERE parent_id = #{parentId}

SELECT c.id, c.from_uid, u.avatar, u.nickname as from_nickname, c.content, c.like_number, c.create_time FROM d_comment c INNER JOIN u_user u ON c.from_uid = u.id WHERE c.is_check = 1 AND parent_id IS NULL
AND type_id = #{query.typeId}
AND type = #{query.type}
ORDER BY c.id DESC
