mapper.select:
select m.id, m.from_uid, m.to_uid, m.msg_type, m.file_id, m.content, m.is_read, m.create_time, m.update_time, u.avatar, u.nickname, fr.file_size, fr.file_url from c_msg m left join u_user u on u.id = m.from_uid left join s_file_record fr on fr.id = m.file_id
msg_type = #{query.msgType}
is_read = #{query.isRead}
and content like concat('%',#{query.content},'%')
and date_format(create_time,'%y%m%d %H:%i:%s') >= date_format(#{query.startTime},'%y%m%d %H:%i:%s')
and date_format(create_time,'%y%m%d %H:%i:%s') <= date_format(#{query.endTime},'%y%m%d %H:%i:%s')
